mapper.update:
update setmeal
image = #{image}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
select * from setmeal left join category on setmeal.category_id = category.id


        and setmeal.name like concat('%',#{name},'%')


        and setmeal.category_id=#{categoryId}


        and setmeal.status=#{status}


order by setmeal.create_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
